import React from 'react';

const Services = () => {
  const services = [
    {
      icon: "üöó",
      title: "Rental Harian/Mingguan",
      description: "Sewa mobil untuk kebutuhan harian atau mingguan dengan harga terjangkau dan fleksibel.",
      features: ["Gratis antar-jemput", "Asuransi lengkap", "24/7 support"]
    },
    {
      icon: "üíº",
      title: "Rental Perusahaan",
      description: "Solusi transportasi untuk kebutuhan bisnis dan perusahaan dengan armada terbaik.",
      features: ["Fleet management", "Laporan berkala", "Dedicated account manager"]
    },
    {
      icon: "‚úàÔ∏è",
      title: "Rental Bandara",
      description: "Layanan khusus untuk penjemputan dan pengantaran di bandara dengan tepat waktu.",
      features: ["Meeting point", "Flight tracking", "Gratis tunggu 2 jam"]
    },
    {
      icon: "üè®",
      title: "Rental Hotel & Wisata",
      description: "Paket khusus untuk kebutuhan wisata dan akomodasi hotel dengan driver profesional.",
      features: ["Tour guide opsional", "Rute wisata", "Flexible itinerary"]
    },
    {
      icon: "üë®‚Äçüë©‚Äçüëß‚Äçüë¶",
      title: "Rental Keluarga",
      description: "Mobil keluarga dengan fitur keamanan lengkap untuk perjalanan nyaman bersama.",
      features: ["Child seat tersedia", "Extra baggage space", "Family-friendly driver"]
    },
    {
      icon: "üîß",
      title: "Maintenance & Support",
      description: "Perawatan berkala dan support 24 jam untuk memastikan kendaraan selalu prima.",
      features: ["Regular check-up", "Emergency support", "Quick response"]
    }
  ];

  return (
    <section id="services" className="section-padding bg-white">
      <div className="container-custom">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-navy-900 mb-4">
            Layanan Kami
          </h2>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Berbagai layanan terbaik untuk memenuhi semua kebutuhan transportasi Anda
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div key={index} className="bg-gradient-to-br from-white to-gray-50 rounded-2xl p-8 shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-100">
              <div className="text-4xl mb-4">{service.icon}</div>
              <h3 className="text-xl font-bold text-navy-900 mb-3">{service.title}</h3>
              <p className="text-gray-600 mb-6">{service.description}</p>
              <ul className="space-y-2">
                {service.features.map((feature, featureIndex) => (
                  <li key={featureIndex} className="flex items-center text-sm text-gray-600">
                    <svg className="w-4 h-4 text-green-500 mr-2 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                    </svg>
                    {feature}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>

        <div className="bg-gradient-to-r from-navy-700 to-navy-800 rounded-2xl p-8 mt-16 text-center text-white">
          <h3 className="text-2xl font-bold mb-4">Butuh Layanan Khusus?</h3>
          <p className="text-navy-100 mb-6 max-w-2xl mx-auto">
            Konsultasikan kebutuhan rental mobil Anda dengan tim profesional kami
          </p>
          <button className="bg-white text-navy-700 hover:bg-gray-100 font-semibold py-3 px-8 rounded-lg transition-colors duration-300">
            Konsultasi Gratis
          </button>
        </div>
      </div>
    </section>
  );
};

export default Services;
