import React, { useState, useEffect } from 'react';
import { Car, Shield, Clock, Award, Zap, MapPin } from 'lucide-react';

const Hero = () => {
  const [scrollY, setScrollY] = useState(0);
  const [activeCard, setActiveCard] = useState(0);

  useEffect(() => {
    const handleScroll = () => setScrollY(window.scrollY);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveCard((prev) => (prev + 1) % 3);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const features = [
    { icon: Shield, label: 'Asuransi Premium', color: 'from-emerald-400 to-teal-500' },
    { icon: Clock, label: 'Antar Jemput 24/7', color: 'from-violet-400 to-purple-500' },
    { icon: Award, label: 'Armada Terbaru', color: 'from-amber-400 to-orange-500' }
  ];

  return (
    <section className="relative min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-800 overflow-hidden flex items-center">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-20 left-10 w-72 h-72 bg-blue-500/10 rounded-full blur-3xl animate-pulse" />
        <div className="absolute bottom-20 right-10 w-96 h-96 bg-purple-500/10 rounded-full blur-3xl animate-pulse delay-700" />
        <div className="absolute top-1/2 left-1/2 w-64 h-64 bg-teal-500/10 rounded-full blur-3xl animate-pulse delay-1000" 
             style={{ transform: `translate(-50%, -50%) translateY(${scrollY * 0.1}px)` }} />

        {/* Grid Pattern */}
        <div className="absolute inset-0 bg-[linear-gradient(rgba(255,255,255,.02)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,.02)_1px,transparent_1px)] bg-[size:100px_100px] [mask-image:radial-gradient(ellipse_80%_50%_at_50%_50%,black,transparent)]" />
      </div>

      <div className="container mx-auto px-6 py-20 relative z-10">
        <div className="grid lg:grid-cols-2 gap-16 items-center">
          {/* Left Content */}
          <div className="space-y-8">
            {/* Badge */}
            <div className="inline-flex items-center gap-2 bg-gradient-to-r from-blue-500/20 to-purple-500/20 backdrop-blur-xl border border-white/10 rounded-full px-6 py-2.5 shadow-2xl">
              <Zap className="w-4 h-4 text-yellow-400 animate-pulse" />
              <span className="text-sm font-semibold text-white">Diskon 30% untuk Booking Pertama</span>
            </div>

            {/* Main Heading */}
            <div className="space-y-4">
              <h1 className="text-6xl lg:text-7xl font-black leading-tight">
                <span className="bg-gradient-to-r from-white via-blue-100 to-purple-200 bg-clip-text text-transparent animate-gradient">
                  Luxury Rides
                </span>
                <br />
                <span className="text-white">On Demand</span>
              </h1>
              <div className="flex items-center gap-3">
                <div className="h-1 w-20 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full" />
                <p className="text-xl text-slate-300">Nikmati kemewahan tanpa batas</p>
              </div>
            </div>

            <p className="text-lg text-slate-400 leading-relaxed max-w-xl">
              Rasakan pengalaman berkendara yang tak terlupakan dengan armada super premium kami. 
              Dari supercar hingga SUV mewah, semua tersedia dengan layanan concierge eksklusif.
            </p>

            {/* CTA Buttons */}
            <div className="flex flex-wrap gap-4">
              <button className="group relative px-8 py-4 bg-gradient-to-r from-blue-600 to-purple-600 rounded-2xl font-bold text-white shadow-2xl shadow-blue-500/50 hover:shadow-purple-500/50 transition-all duration-300 hover:scale-105 overflow-hidden">
                <span className="relative z-10 flex items-center gap-2">
                  <Car className="w-5 h-5" />
                  Mulai Petualangan
                </span>
                <div className="absolute inset-0 bg-gradient-to-r from-purple-600 to-pink-600 opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
              </button>

              <button className="group px-8 py-4 bg-white/5 backdrop-blur-xl border-2 border-white/10 rounded-2xl font-bold text-white hover:bg-white/10 hover:border-white/20 transition-all duration-300 flex items-center gap-2">
                <MapPin className="w-5 h-5" />
                Lihat Koleksi
              </button>
            </div>

            {/* Stats */}
            <div className="grid grid-cols-3 gap-6 pt-8">
              {[
                { value: '800+', label: 'Kendaraan Elite' },
                { value: '25K+', label: 'Happy Drivers' },
                { value: '15+', label: 'Kota Tersedia' }
              ].map((stat, idx) => (
                <div key={idx} className="group">
                  <div className="text-4xl font-black bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent mb-1">
                    {stat.value}
                  </div>
                  <div className="text-sm text-slate-500 group-hover:text-slate-300 transition-colors">
                    {stat.label}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Right Visual */}
          <div className="relative lg:h-[600px]">
            {/* Main Card */}
            <div className="relative" style={{ transform: `translateY(${scrollY * -0.05}px)` }}>
              <div className="bg-gradient-to-br from-white/10 to-white/5 backdrop-blur-2xl rounded-3xl p-8 border border-white/20 shadow-2xl">
                {/* Car Visualization */}
                <div className="aspect-video bg-gradient-to-br from-slate-800 to-slate-900 rounded-2xl mb-6 relative overflow-hidden group">
                  <div className="absolute inset-0 bg-gradient-to-t from-blue-500/20 to-transparent" />
                  <div className="absolute inset-0 flex items-center justify-center">
                    <div className="relative">
                      <div className="absolute inset-0 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full blur-3xl opacity-50 animate-pulse" />
                      <Car className="w-32 h-32 text-white relative z-10 transform group-hover:scale-110 transition-transform duration-500" />
                    </div>
                  </div>
                  {/* Floating particles */}
                  <div className="absolute top-1/4 left-1/4 w-2 h-2 bg-blue-400 rounded-full animate-ping" />
                  <div className="absolute top-3/4 right-1/4 w-2 h-2 bg-purple-400 rounded-full animate-ping delay-500" />
                </div>

                {/* Feature Cards */}
                <div className="space-y-3">
                  {features.map((feature, idx) => {
                    const Icon = feature.icon;
                    return (
                      <div
                        key={idx}
                        className={`flex items-center gap-4 p-4 rounded-xl transition-all duration-500 cursor-pointer ${
                          activeCard === idx
                            ? 'bg-gradient-to-r from-white/20 to-white/10 border border-white/30 scale-105'
                            : 'bg-white/5 border border-white/10 hover:bg-white/10'
                        }`}
                        onMouseEnter={() => setActiveCard(idx)}
                      >
                        <div className={`w-12 h-12 rounded-xl bg-gradient-to-br ${feature.color} flex items-center justify-center shadow-lg`}>
                          <Icon className="w-6 h-6 text-white" />
                        </div>
                        <div className="flex-1">
                          <div className="font-bold text-white">{feature.label}</div>
                          <div className="text-sm text-slate-400">Premium Service</div>
                        </div>
                        <div className={`w-2 h-2 rounded-full ${activeCard === idx ? 'bg-green-400' : 'bg-slate-600'} transition-colors`} />
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>

            {/* Floating Badges */}
            <div 
              className="absolute -top-6 -right-6 bg-gradient-to-br from-emerald-500 to-teal-600 p-4 rounded-2xl shadow-2xl shadow-emerald-500/50 rotate-6 hover:rotate-12 transition-transform duration-300"
              style={{ transform: `rotate(6deg) translateY(${scrollY * 0.02}px)` }}
            >
              <div className="text-sm font-black text-white">Tersedia 24/7</div>
            </div>

            <div 
              className="absolute -bottom-6 -left-6 bg-gradient-to-br from-amber-500 to-orange-600 p-4 rounded-2xl shadow-2xl shadow-amber-500/50 -rotate-6 hover:-rotate-12 transition-transform duration-300"
              style={{ transform: `rotate(-6deg) translateY(${scrollY * -0.02}px)` }}
            >
              <div className="text-sm font-black text-white">Harga Spesial</div>
            </div>

            <div 
              className="absolute top-1/3 -left-12 bg-gradient-to-br from-violet-500 to-purple-600 p-4 rounded-2xl shadow-2xl shadow-violet-500/50 rotate-3 hover:rotate-6 transition-transform duration-300"
              style={{ transform: `rotate(3deg) translateY(${scrollY * 0.03}px)` }}
            >
              <Shield className="w-6 h-6 text-white" />
            </div>
          </div>
        </div>
      </div>

      {/* Bottom Wave */}
      <div className="absolute bottom-0 left-0 right-0 h-32 bg-gradient-to-t from-slate-950 to-transparent" />

      <style jsx>{`
        @keyframes gradient {
          0%, 100% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
        }
        .animate-gradient {
          background-size: 200% auto;
          animation: gradient 3s ease infinite;
        }
        .delay-500 { animation-delay: 500ms; }
        .delay-700 { animation-delay: 700ms; }
        .delay-1000 { animation-delay: 1000ms; }
      `}</style>
    </section>
  );
};

export default Hero;
